var wfe = window.wfe || window.parent.wfe;

var wfePopup = {
    getParam: function(n, dv) {
        return this.editor.getParam(n, dv);
    },
    getLang: function(n, dv) {
        var lang = Wf.getLanguage();
        return Wf.i18n[lang + '.' + n] || dv;
    },
    getWindowArg: function(n, dv) {
        var v = this.editor.windowManager.params[n];
        return (typeof v !== "undefined") ? v : dv;
    },
    close: function() {
        return this.editor.windowManager.close();
    }
};

// fake editor
wfePopup.editor = wfe;

jQuery(document).ready(function($) {
    var win = window.parent,
        s = wfePopup.editor.settings,
        el;

    if (s.element) {
        el = win.document.getElementById(s.element);

        if (el) {
            wfePopup.editor.windowManager.params.value = el.value;
        }
    }

    if (s.callback) {
        var fn = win[s.callback];

        if (typeof fn === "function") {
            wfePopup.editor.windowManager.params.callback = fn;
        }
    }

    if (!el && !s.callback) {
        $('#cancel, #insert').hide();
    }
});